mapper.insert:
INSERT INTO MP_BOARD(    BNO 
					      , TITLE 
					      , CONTENT 
					      , WRITER
					      , USER_ID  )
              VALUES(    MP_BOARD_SEQ.NEXTVAL 
              		  , #{title}
              		  , #{content}
              		  , #{writer}
              		  , #{user_id}  )

mapper.update:
	UPDATE MP_BOARD
   SET TITLE    =   #{title},
   	   CONTENT  =   #{content}
 WHERE BNO = #{bno}

	UPDATE MP_BOARD SET
HIT = HIT+1
WHERE BNO = #{bno}
