mapper.select:

	  select 
		 no,activityname, writer, meetingtime, meetingplace, peoplenum,peoplemaxnum,regdate,regular,image
	  from (
	    select

	    	/*+INDEX_DESC(mate pk_mate ) */

 		 rownum rn, no, activityname, writer, meetingtime, meetingplace, peoplenum,peoplemaxnum,regdate,regular,image
	      from 


						(select * from mate order by meetingtime asc)	


						(select * from mate order by likecnt desc)	


				mate



	  where sysdate <meetingtime and



	      rownum <= #{pageNum} * #{amount}

	      )  

	  where rn > (#{pageNum} -1) * #{amount}
