mapper.select:

  select 
	fno,activityname, writer, meetingtime, meetingplace, peoplenum,peoplemaxnum,regdate
  from (
     select /*+INDEX_DESC(flashmate pk_flash ) */
        rownum rn, fno, activityname, writer, meetingtime, meetingplace, peoplenum,peoplemaxnum,regdate 
      from flashmate
  where 



      rownum <= #{pageNum} * #{amount}
      )
  where rn > (#{pageNum} -1) * #{amount}
